import { useState } from 'react';
import './App.css';
import LoginForm from './Components/LoginForm/LoginForm';
import RegisterForm from './Components/RegisterForm/RegisterForm';

function App() {
  const [estatico, levantado] = useState(true);

  return (
<div 
onMouseEnter={() => levantado(true)}
onMouseLeave={() => levantado(false)}
style={{display: 'flex', gap: '35px' }}>

<LoginForm/>
<RegisterForm/>

{estatico ? null: <LoginForm></LoginForm>}
</div>
  );
}

export default App;
